import{_ as t,c as e,o as i,a as o}from"./app.04384240.js";const s="/assets/quiz-shot.360c883a.png",n="/assets/quiz-list.7b27401b.png",r="/assets/quiz-form.1a6c130e.png",a="/assets/quiz-list-data.97c7d3f1.png",q=JSON.parse('{"title":"Quiz","description":"","frontmatter":{},"headers":[],"relativePath":"course-creation/create-a-quiz.md","lastUpdated":1667542423000}'),c={name:"course-creation/create-a-quiz.md"},u=o('<h1 id="quiz" tabindex="-1">Quiz <a class="header-anchor" href="#quiz" aria-hidden="true">#</a></h1><p>Quiz help make lessons interactive and ensures a quick revision of concepts learned previously.</p><p><img src="'+s+'" alt="Quiz"></p><p>To create a quiz, click on the <strong>Create Quiz</strong> button from the lesson page. You will be redirected to the Quiz List. If you have not created any quizzes yet, the list will be empty. Click on <strong>Add Quiz</strong> to create a new quiz. You will be redirected to the quiz form.</p><p><img src="'+n+'" alt="Quiz List"></p><p>Enter the following information in the quiz form:</p><ul><li><strong>Quiz Title:</strong> Give your quiz a relevant title.</li><li><strong>Question:</strong> Enter the question in this field.</li><li><strong>Options:</strong> Provide options for the question in this field. A question should have a minimum of 2 options and a maximum of 4 options.</li><li><strong>Explanation:</strong> You can explain why an option is correct or incorrect using this field.</li><li><strong>Is Correct:</strong> If an option is correct you can check this field to indicate the same. A question can have more than one correct option.</li></ul><p><img src="'+r+'" alt="Quiz Form"></p><div class="tip custom-block"><p class="custom-block-title">Add more questions</p><p>To add more questions click on the <strong>New Question</strong> button.</p></div><p>Once you have entered all the information you can save the quiz. You will notice an entry for the quiz in the quiz list. You can copy the quiz id from the quiz list using the <strong>Copy Quiz ID</strong> button. Then you can paste it in the lessons Quiz ID field where you want the quiz.</p><p><img src="'+a+'" alt="Quiz List"></p>',11),l=[u];function p(d,h,m,z,_,g){return i(),e("div",null,l)}const Q=t(c,[["render",p]]);export{q as __pageData,Q as default};
