import{_ as e,c as t,o as a,a as r}from"./app.0a34edb4.js";const s="/assets/course-list.8e243b40.png",g=JSON.parse('{"title":"Introduction","description":"","frontmatter":{},"headers":[{"level":2,"title":"What can you do with Frappe LMS","slug":"what-can-you-do-with-frappe-lms","link":"#what-can-you-do-with-frappe-lms","children":[]},{"level":2,"title":"Who can use Frappe LMS","slug":"who-can-use-frappe-lms","link":"#who-can-use-frappe-lms","children":[]},{"level":2,"title":"Platforms using Frappe LMS","slug":"platforms-using-frappe-lms","link":"#platforms-using-frappe-lms","children":[]},{"level":2,"title":"Get Started","slug":"get-started","link":"#get-started","children":[]}],"relativePath":"index.md","lastUpdated":1698773724000}'),n={name:"index.md"},o=r('<h1 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-hidden="true">#</a></h1><p>Frappe LMS is an easy-to-use, open-source Learning Management System. It helps you set up your own learning platform.</p><p><img src="'+s+'" alt="Courses"></p><h2 id="what-can-you-do-with-frappe-lms" tabindex="-1">What can you do with Frappe LMS <a class="header-anchor" href="#what-can-you-do-with-frappe-lms" aria-hidden="true">#</a></h2><p>\u{1F4DA}\xA0Create and share online courses with your target audience.</p><p>\u{1F4DD}\xA0Create interesting and interactive lessons in the form of text, videos, quizzes, etc.</p><p>\u{1F4AC}\xA0Solve queries of your students using the discussion section.</p><p>\u{1F3DB}\xA0Group your students into batches to track their progress better</p><p>\u{1F4BB}\xA0Conduct live classes by integrating your zoom account with the LMS</p><p>\u{1F4C8}\xA0Keep a watch on important numbers like signup and enrolment count using the Statistics Dashboard</p><p>\u{1F4BC}\xA0Help your students find career opportunities using the Job Board.</p><h2 id="who-can-use-frappe-lms" tabindex="-1">Who can use Frappe LMS <a class="header-anchor" href="#who-can-use-frappe-lms" aria-hidden="true">#</a></h2><p>\u{1F680}\xA0Organisations wanting to train their employees internally</p><p>\u{1F680}\xA0Company\u2019s wanting to train their users about their products</p><p>\u{1F680}\xA0Schools and Universities who wish to have their study material available online for their students.</p><p>\u{1F680}\xA0Entrepreneurs/Freelancers/Influencers who want to share their knowledge and experience with others out there.</p><h2 id="platforms-using-frappe-lms" tabindex="-1">Platforms using Frappe LMS <a class="header-anchor" href="#platforms-using-frappe-lms" aria-hidden="true">#</a></h2><ul><li><a href="https://frappe.school/courses" target="_blank" rel="noreferrer">Frappe School</a></li><li><a href="https://mon.school/courses" target="_blank" rel="noreferrer">Mon School</a></li><li><a href="https://tinkerhub.frappe.cloud/courses" target="_blank" rel="noreferrer">Tinkerhub</a></li></ul><h2 id="get-started" tabindex="-1">Get Started <a class="header-anchor" href="#get-started" aria-hidden="true">#</a></h2><ul><li><a href="./get-started/setup-your-account.html">Setup your account</a></li><li><a href="./course-creation/create-a-course.html">Create a Course</a></li><li><a href="./course-creation/add-a-chapter.html">Add a Chapter</a></li><li><a href="./course-creation/add-a-lesson.html">Add a Lesson</a></li></ul>',20),i=[o];function l(p,h,c,u,d,f){return a(),t("div",null,i)}const _=e(n,[["render",l]]);export{g as __pageData,_ as default};
